import React from 'react';
import '../styleSheets/SongSearchBar.css';

import {
  Container,
  Navbar,
  NavbarBrand,
  Row,
  Jumbotron,
  InputGroup,
  InputGroupAddon,
  Button,
  FormGroup,
  ListGroup,
  ListGroupItem,
  Input,
  Col
} from 'reactstrap';

class StyleUnderDurationSearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        danceStyle: '',
        duration: '',
        songList: []
    };

    this.handleChange = this.handleChange.bind(this);
    this.styleDurationSearch = this.styleDurationSearch.bind(this);
  }

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  styleDurationSearch(event) {
    
  }

  updateDanceList(event) {
    this.handleChange(event);
    this.getDanceByStyle(event.target.value);
  }

  getStyleList = () => {
    fetch('/api/dance/style')
    .then(res => res.json())
    .then(res => {
      var styleList = res.map(r => r.dancestyle);
      this.setState({ styleList });
    });
  };

  componentDidMount () {
    this.getStyleList();
  }

  render() {
    return (
        <div>
            <FormGroup>
                <Input name="danceStyle" value={this.state.danceStyle} placeholder="Select a Dance Style" type="select" onChange={this.handleChange}>
                { this.state.styleList.length === 0 && <option>No Dance Style Selected yet.</option> }
                { this.state.styleList.length > 0 && <option>Any Style</option> }
                { this.state.styleList.map((style, i) => <option key={i}>{style}</option>) }
                </Input>
            </FormGroup>
            <FormGroup>
                <Input name="duration" value={this.state.duration} placeholder="Maximum Duration" type="select" onChange={this.handleChange}>
                { this.state.danceList.length === 0 && <option>No Dance Selected yet.</option> }
                { this.state.danceList.length > 0 && <option>Select a Dance.</option> }
                { this.state.danceList.map((dance, i) => <option name="dances" key={this.state.danceList[i]}>{dance}</option>) }
                </Input>
            </FormGroup>
        </div>
    );
  }
}

export default StyleUnderDurationSearchBar;
