import React from 'react';
import '../styleSheets/SongSearchBar.css';

class SongSearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      song: '',
      artist: '',
      danceList: []
    };

    //this.handleClick = this.handleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.searchSong = this.searchSong.bind(this);
  }

  /*handleClick(event) {
    this.setState({ [event.target.name]: '' });
  }*/

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  getSongList = () => {
    fetch('/api/song')
    .then(res => res.json())
    .then(res => {
      var songList = res.map(r => r.city_name);
      this.setState({ songList });
    });
  };

  getSong = (song) => {
    fetch(`/api/song/${song}`)
    .then(res => res.json())
    .then(res => {
      var songList = res.map(r => r.city_name);
      this.setState({ songList });
    });
  }

  searchSong(event) {



    alert(
      'A search was submitted: ' + this.state.song + ' by ' + this.state.artist
    );
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.searchSong}>
        <div>
          <input
            name="song"
            placeholder="Search for a Song by Title ..."
            className="field"
            type="text"
            value={this.state.song}
            //onClick={this.handleClick}
            onChange={this.handleChange}
          />
          <p> &amp; </p>
          <input
            name="artist"
           placeholder="Search for a Song by Artist ..."
            className="field"
            type="text"
            value={this.state.artist}
            //onClick={this.handleClick}
            onChange={this.handleChange}
          />
          <input className="button" type="submit" value="Submit" />
        </div>
      </form>
    );
  }
}

export default SongSearchBar;
