import React from 'react';
import '../styleSheets/SongSearchBar.css';

import {
  Container,
  Navbar,
  NavbarBrand,
  Row,
  Jumbotron,
  InputGroup,
  InputGroupAddon,
  Button,
  FormGroup,
  ListGroup,
  ListGroupItem,
  Input,
  Col
} from 'reactstrap';

class DanceSongCount extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        danceCountList: []
    };

    this.handleChange = this.handleChange.bind(this);
    this.updateDanceList = this.updateDanceList.bind(this);
  }

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  updateDanceList(event) {
    this.handleChange(event);
    this.getDanceByStyle(event.target.value);
  }

  getDanceList = () => {
    fetch('/api/dance')
    .then(res => res.json())
    .then(res => {
      var danceList = res.map(r => r.dancename);
      this.setState({ danceList });
    });
  };

  getDanceSongCount = (danceName) => {
      fetch(`/api/dance/${danceName}`)
      .then(res => res.json())
      .then(res => {
      var danceList = res.map(r => r.dancename);
      this.setState({ danceList });
    });
  }

  componentDidMount () {
    this.getDanceList();
  }

  render() {
    return (
        <div>

            <FormGroup>
                <Input name="danceName" value={this.state.danceName} placeholder="Select a Dance" type="select" onChange={this.handleChange}>
                { this.state.danceCountList.length === 0 && <option>No Dance Selected yet.</option> }
                { this.state.danceCountList.length > 0 && <option>Select a Dance.</option> }
                { this.state.danceCountList.map((dance, i) => <option name="dances" key={this.state.danceList[i]}>{dance}</option>) }
                </Input>
            </FormGroup>
        </div>
    );
  }
}

export default DanceSongCount;
